INSERT INTO infos VALUES(2, 2, 'Нью-Йорк', NULL, NULL, NULL, '', '', '', 'Город расположен на берегу Атлантического океана на нескольких островах и полуострове. Он возник возле устья реки Гудзон, которая перерезает горную цепь Аппалачей, открывая путь внутрь страны. У Нью-Йорка Гудзон разделяется на рукава: Гарлем, Ист-Ривер и другие. А ниже он расширяется, образуя Верхнюю бухту площадью 40 квадратных километров и Нижнюю бухту — 200 квадратных километров.', 0, '', 2, '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', '', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
